mapper.select:
select routeDetail.id id, routeDetail.route_id routeId, routeDetail.apply_num applyNum, routeDetail.route_status routeStatus, routeDetail.start_time startTime, routeDetail.end_time endTime, route.id "route.id", route.title "route.title", route.pic_url "route.picUrl" FROM t_route route INNER JOIN t_route_detail routeDetail ON route.id=routeDetail.route_id
route.status='ENABLED' AND routeDetail.id in ( SELECT id FROM ( SELECT detail.id,detail.route_id FROM t_route_detail detail ORDER BY case when detail.route_status ='FULLED' then 0 else 1 end DESC ,detail.start_time ASC )temp GROUP BY route_id )
and route.routeType=#{routeType}
ORDER BY routeDetail.start_time ASC
limit #{limit}

select routeDetail.id id, routeDetail.route_id routeId, routeDetail.apply_num applyNum, routeDetail.route_status routeStatus, routeDetail.start_time startTime, routeDetail.end_time endTime, route.id "route.id", route.title "route.title", route.pic_url "route.picUrl" FROM t_order o INNER JOIN t_route_detail routeDetail ON o.route_detail_id=routeDetail.id INNER JOIN t_route route ON routeDetail.route_id=route.id
o.order_status='PAYED' AND route.status='ENABLED' AND o.user_id=#{userId}
AND routeDetail.end_time < now()
AND routeDetail.end_time >= now()
ORDER BY routeDetail.start_time ASC
limit #{limit}
